declare module 'datagram-stream' {
  import { Stream } from 'stream';

  interface ICreateStreamOptions {
    address: string;
    port: number
    bindingPort?: number
    unicast?: string;
    broadcast?: string;
    multicast?: string;
    multicastTTL?: number
    loopback?: boolean;
    reuseAddr?: boolean;
  }

  export default function createDatagramStream(options: ICreateStreamOptions): Stream;
}
